import React from 'react';
import ItemsCarousel from 'react-items-carousel';
import range from 'lodash/range';

export default class Content01 extends React.Component {

    componentWillMount() {
      this.setState({
        children: [
            {
                title: '무직',
                content: '무직, 학생, 취업준비생, 아르바이트생 등',
                limitStart: 100,
                limitEnd: 1500
            },
            {
                title: '연체자',
                content: '대출, 카드, 통신 등 연체이력 보유자',
                limitStart: 100,
                limitEnd: 300
            },
            {
                title: '군미필',
                content: '신체검사를 받은 군미필자',
                limitStart: 100,
                limitEnd: 300
            },
            {
                title: '학생',
                content: '대학생, 휴학생, 복학생 등',
                limitStart: 100,
                limitEnd: 1500
            },
            {
                title: '무직',
                content: '무직, 학생, 취업준비생, 아르바이트생 등',
                limitStart: 100,
                limitEnd: 1500
            },
            {
                title: '주부',
                content: "등초본상 'OOO의 처",
                limitStart: 100,
                limitEnd: 3000
            },
            {
                title: '사업자(일수)',
                content: '사업장 운영중인 사장님들',
                limitStart: 100,
                limitEnd: 20000
            },
            {
                title: '직장인/프리랜서',
                content: '1개월이상 재직중인 4대가입직장인(프리랜서가능)',
                limitStart: 100,
                limitEnd: 10000
            },
        ],
        activeItemIndex: 0,
      });
    }
  
    createChildren = n => range(n).map(i => <div key={i} style={{ height: 200, background: '#333' }}>{i}</div>);
  
    changeActiveItem = (activeItemIndex) => this.setState({ activeItemIndex });
  
    render() {
      const {
        activeItemIndex,
        children,
      } = this.state;
  
      return (
        <ItemsCarousel
          // Placeholder configurations
          enablePlaceholder
          numberOfPlaceholderItems={5}
          minimumPlaceholderTime={1000}
          placeholderItem={<div style={{ height: 200, background: '#900' }}>children</div>}
  
          // Carousel configurations
          numberOfCards={3}
          gutter={12}
          showSlither={true}
          firstAndLastGutter={true}
          freeScrolling={false}
  
          // Active item configurations
          requestToChangeActive={this.changeActiveItem}
          activeItemIndex={activeItemIndex}
          activePosition={'center'}
  
          chevronWidth={24}
          rightChevron={'>'}
          leftChevron={'<'}
          outsideChevron={false}
        >
          {children}
        </ItemsCarousel>
      );  
    }
  }
